import React, { Component } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route} from "react-router-dom";
import { Link } from 'react-router-dom';

import NavBar from "./navbar.component";

const GameName = props => (
  
  <h2>{props.game.name}</h2>
  
)

export default class TestPage extends Component{
  constructor(props) {
    super(props);

  }

  componentDidMount() {
    axios.get('http://localhost:5000/games/' + this.props.location.id)
      .then(res => {
        console.log(res.data);
        this.setState({game: res.data});
      })
      .catch((error) => {
        console.Console.log(error);
      })
  }

  getGameName() {
    return <GameName game={this.state.game} />
  }

  render() {
    return(
      <div>
        <NavBar />
        <div className="page">
          <div className="gameTileGrid"> {/*This gameTileGrid goes into another grid that organizes all the elements on this page */}
            <h1 className="headingText">Featured Games</h1>
              {this.getGameName()}
          </div>
        </div>
      </div>
    );
  }
}
