import React, { Component } from 'react';
import axios from 'axios';
import "./style-sheets/gamepage.css";

import NavBar from "./navbar.component";

const Post = props => (
    <div>
      <div className="post">
        <div className="postHeader">
          <div className="headerCircle"></div>
          <div className="posterName">
            {props.post.name}
            <br></br>
            <div className="postDate">{props.post.date.split("T")[0]}</div>
          </div>
        </div>
        <div className="contentBox">
          <div className="contentText">{props.post.message}</div>
        </div>
      </div>
    </div>
)

const createPost = props => (
  <form noValidate onSubmit={this.onSubmit}>
    <div className="input-field col s12">
      <input
        onChange={this.onChange}
        value={this.state.email}
        id="post"
      />
      <label htmlFor="email">Create a Post Here</label>
    </div>
    <div className="col s12" style={{ paddingLeft: "11.250px" }}>
      <button type="submit">Login</button>
    </div>
  </form>
)

export default class TestPage extends Component{
  constructor(props) {
    super(props);

    this.state = {game: [], posts: [], id: this.props.location.id}
  }

  componentDidMount() {
    axios.get('/games/' + this.props.location.id)
      .then(res => {
        this.setState({game: res.data})
        this.getPosts(this.state.game._id);
      })
      .catch((error) => {
        console.log(error);
      })
      
  }

  onSubmit = e => {
    e.preventDefault();
    
    
  };

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  getPosts(gameId){
    axios.get('/posts/' + gameId)
      .then(res => {
        this.setState({posts: res.data.reverse()});
      })
      .catch((error) => {
        console.log(error);
      })
  }

  createPosts(){
    return this.state.posts.map(currentPost =>{
      return <Post post={currentPost} key={currentPost._id} />
    })
  }

  createCreatePost(){
    return <createPost />
  }

  render() {
    return(
      <div className="background">
        <NavBar />
        <div className="page">
          <div className="pageGrid">
            <div className="leftColumn">
              <div className="gameTitle">
                {this.state.game.name}
              </div>
              <div className="popularPosts">
                <p style={{color: 'white', marginLeft: '10px'}}>Popular Posts</p>
              </div>
              <div className="popularPosters">
                <p style={{color: 'white', marginLeft: '10px'}}>Popular Posters</p>
              </div>
            </div>
            <div className="middleColumn">
              <div className="feed">
                {this.createCreatePost()}
                {this.createPosts()}
              </div>
            </div>
            <div className="leftColumn">
            <h1 style={{color: 'white', position: 'fixed'}}>{this.state.game.name}</h1>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
