class Login extends Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      errors: {}
    };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.auth.isAuthenticated) {
      this.props.history.push("/"); // push user to dashboard when they login
    }
    
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

  componentDidMount() {
    // If logged in and user navigates to Login page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
      this.props.history.push("/");
    }
  }
  
  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };
  
  onSubmit = e => {
    e.preventDefault();
    
    const userData = {
      email: this.state.email,
      password: this.state.password
    };
    
    this.props.loginUser(userData);

    console.log(userData);
  };
  
  render() {
    const { errors } = this.state;
    return (
      <div className="pageBackground">
        <div className="container">
            <div style={{ marginTop: "4rem" }} className="row">
              <div style={{width: "50%"}}>
                <Link to="/" className="backToHome">
                  <i className="material-icons left">keyboard_backspace</i> Back to
                  home
                </Link>
                <div>
                  <h4 style={{color: "white"}}>
                    <b>Login</b> to <b style={{color: "#8015E8"}}>PlayerPlusPlus</b>
                  </h4>
                  <p style={{color: "white"}}>
                    Don't have an account? <Link to="/register" className="register">Click Here to Register.</Link>
                  </p>
                </div>
                <form noValidate onSubmit={this.onSubmit}>
                  <div className="form-group">
                    <label for="email" style={{color: "white"}}>Email address</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.email}
                      error={errors.email}
                      id="email"
                      type="email"
                      className={classnames("form-control", {
                        invalid: errors.email || errors.emailnotfound
                      })}
                    />
                    <span className="red-text">
                      {errors.email}
                      {errors.emailnotfound}
                    </span>
                  </div>
                  <div className="form-group">
                    <label for="password" style={{color: "white"}}>Password</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.password}
                      error={errors.password}
                      id="password"
                      type="password"
                      className={classnames("form-control", {
                        invalid: errors.password || errors.passwordincorrect
                      })}
                    />
                    <span className="red-text">
                      {errors.password}
                      {errors.passwordincorrect}
                    </span>
                  </div>
                  <div>
                    <button type="submit" className="btn btn-lg loginPageButton">Login</button>
                  </div>
                </form>
              </div>
            </div>
        </div>
      </div>
    );
  }
}

Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { loginUser }
)(Login);
